import React, { Component} from "react";
import Alert from "react-bootstrap/Alert";
import { Row, Col} from "react-bootstrap";

class Latecny extends Component{
    state = {
        latency: null,
        ros: null,
    };

    componentDidMount() {
        if (this.props.ros) {
            this.setState({ ros: this.props.ros }, () => {
                this.startLatencyMeasurement();
            });
        }
    }


    componentDidUpdate(prevProps) {
        // Aktualisiere die ROS-Verbindung, falls sie sich ändert
        if (this.props.ros !== prevProps.ros) {
            this.setState({ ros: this.props.ros }, () => {
                this.startLatencyMeasurement();
            });
        }
    }

    startLatencyMeasurement() {
        const { ros } = this.state;

        if (!ros) {
            console.warn("No ROS connection available for latency measurement.");
            return;
        }

        // Erstelle ein ROS-TOPIC für die Latenz
        const latency = new window.ROSLIB.Topic({
            ros: ros,
            name: "/latency_ping", // Server muss dieses Topic behandeln
            messageType: "std_msgs/String",
        });

        // Sende regelmäßig Pings und berechne die Latenz
        setInterval(() => {
            const startTime = Date.now(); // Zeitstempel beim Senden

            // Sende eine Nachricht
            const latency_msg = new window.ROSLIB.Message({
                data: "ping",
            });
            latency.publish(latency_msg);

            // Warte auf die Antwort
            latency.subscribe((response) => {
                const endTime = Date.now(); // Zeitstempel beim Empfang
                const latency = endTime - startTime; // Latenz berechnen
                this.setState({ latency }); // Latenz im State speichern
                latency.unsubscribe(); // Nur einmal auf die Antwort warten
            });
        }, 5000); // Alle 5 Sekunden messen
    }


    render(){
        return(
            <div>
                <Row className="mb-5">
                    <Col >
                        <h3 className="mt-4">Latecny</h3>
                        <p className="mt-0">
                            {this.state.latency !== null? ` ${this.state.latency} ms` : "Messung läuft..."}
                        </p>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default Latecny;
