import React, { Component} from "react";
import { Row, Col} from "react-bootstrap";
import Config from "../scripts/config"

class RobotState extends Component{
    state = {
        ros: null,
        x: 0,
        y: 0,
        orientation: 0,
        Linear_velocity: 0,
        angular_velocity: 0,
    };


    componentDidMount() {
        if (this.props.ros) {
            this.setState({ ros: this.props.ros }, () => {
                this.pose_subscriber();
            });
        }
    }


    componentDidUpdate(prevProps) {
        // Aktualisiere die ROS-Verbindung, falls sie sich ändert
        if (this.props.ros !== prevProps.ros) {
            this.setState({ ros: this.props.ros }, () => {
                this.pose_subscriber();
            });
        }
    }


