import React, { Component} from "react";
import { Row, Col} from "react-bootstrap";
import Config from "../scripts/config"

class RobotState extends Component{
    state = {
        ros: null,
        x: 0,
        y: 0,
        orientation: 0,
        linear_velocity: 0,
        angular_velocity: 0,
    };


    componentDidMount() {
        if (this.props.ros) {
            this.setState({ ros: this.props.ros }, () => {
                this.pose_subscriber();
            });
        }
    }


    componentDidUpdate(prevProps) {
        // Aktualisiere die ROS-Verbindung, falls sie sich ändert
        if (this.props.ros !== prevProps.ros) {
            this.setState({ ros: this.props.ros }, () => {
                this.pose_subscriber();
            });
        }
    }



    pose_subscriber(){
        const {ros} = this.state;
        if (!ros){
            console.warn("No ROS connection available for pose subscription.")
            return;
        }

        // pose ROS2 Subscriber
        const pose_topic = new window.ROSLIB.Topic({
            ros: ros,
            name: Config.ODOM_TOPIC ,
            messageType: Config.ODOM_MSG_TYPE ,
        });

        // pose callback
        pose_topic.subscribe((msg) =>{
            console.log("Pose message received: ", msg);

            // Aktualisiere den Zustand basierend auf der empfangenen Nachricht
            this.setState({
                x: msg.pose.pose.position.x,
                y:  msg.pose.pose.position.y,
                orientation: msg.pose.pose.orientation.z,
            });
        });
    }


    render(){
        return(
            <div>
                <Row className="mb-5">
                    <Col >
                        <h3 className="mt-4">position</h3>
                        <p className="mt-0"> x: {this.state.x.toFixed(2)} </p>
                        <p className="mt-0"> y: {this.state.y.toFixed(2)} </p>
                        <p className="mt-0"> Orientation: {this.state.orientation.toFixed(2)} </p>
                    </Col>
                </Row>

                <Row>
                    <Col >
                        <h3 className="mt-5">Velocities</h3>
                        <p className="mt-0"> Linear Velocity: {this.state.linear_velocity.toFixed(2)} </p>
                        <p className="mt-0"> Angular Velocity: {this.state.angular_velocity.toFixed(2)} </p>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default RobotState;
