import React, { Component} from "react";
import { Row, Col} from "react-bootstrap";
import Config from "../scripts/config"

class RobotState extends Component{
    state = {
        ros: null,
        x: 0,
        y: 0,
        orientation: 0,
        linear_velocity: 0,
        angular_velocity: 0,
    };


    componentDidMount() {
        if (this.props.ros) {
            this.setState({ ros: this.props.ros }, () => {
                this.pose_subscriber();
            });
        }
    }


    componentDidUpdate(prevProps) {
        // Aktualisiere die ROS-Verbindung, falls sie sich ändert
        if (this.props.ros !== prevProps.ros) {
            this.setState({ ros: this.props.ros }, () => {
                this.pose_subscriber();
            });
        }
    }


    componentWillUnmount() {
        // Unsubscribe, falls vorhanden
        if (this.pose_topic) {
            this.pose_topic.unsubscribe();
        }
    }


    pose_subscriber(){
        const {ros} = this.state;
        if (!ros){
            console.warn("No ROS connection available for pose subscription.")
            return;
        }

        // odom ROS2 Subscriber for the velocit
        this.pose_topic = new window.ROSLIB.Topic({
            ros: ros,
            name: "/odometry/filtered",
            messageType: Config.ODOM_MSG_TYPE,
        });

        // pose callback
        this.pose_topic.subscribe((message) => {
            try{
                console.log("Pose message received: ", message);

                // Aktualisiere den Zustand basierend auf der empfangenen Nachricht
                this.setState({
                    x: message.pose.pose.position.x.toFixed(2),
                    y:  message.pose.pose.position.y.toFixed(2),
                    // orientation: message.pose.pose.orientation.z.toFixed(2),
                });
            }
            catch(error){
                console.error("Error processing pose message:", error);
            }
        });
    }


    render(){
        return(
            <div>
                <Row className="mb-5">
                    <Col >
                        <h3 className="mt-4">Position</h3>
                        <p className="mt-0"> x: {this.state.x} </p>
                        <p className="mt-0"> y: {this.state.y} </p>
                        <p className="mt-0"> Orientation: {this.state.orientation} </p>
                    </Col>
                </Row>

                <Row>
                    <Col >
                        <h3 className="mt-5">Velocities</h3>
                        <p className="mt-0"> Linear Velocity: {this.state.linear_velocity} </p>
                        <p className="mt-0"> Angular Velocity: {this.state.angular_velocity} </p>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default RobotState;
