// source: https://github.com/RobotWebTools/roslibjs/blob/ros2/examples/node_simple.js

import React, { Component} from "react";
import { Joystick } from "react-joystick-component";
import Config from "../scripts/config"

class Teleoperation extends Component{
    constructor(props) {
        super(props);
        this.handleMove = this.handleMove.bind(this);
        this.handleStop = this.handleStop.bind(this);
    }

    handleMove(){
        console.log("handle move");

        // Only proceed if we have a ROS connection
        if (!this.props.ros) {
            console.warn("No ROS connection available");
            return;
        }

        // cmd_vel_web ROS2 publisher
        var cmd_vel_web = new window.ROSLIB.Topic({
            ros : this.props.ros,
            name : Config.CMD_VEL_TOPIC,
            messageType : Config.CMD_VEL_MSG_TYPE,
        });

        // Create Twist message
        var twist = new window.ROSLIB.Message({
            linear:{x: 1.0, y: 0.0, z: 0.0
            },
            angular:{
                x: 0.0,
                y: 0.0,
                z: 1.5
            },
        });

        // publish Twist message
        cmd_vel_web.publish(twist);
    }


    handleStop(){
        console.log("handle stop");

       // Only proceed if we have a ROS connection
        if (!this.props.ros) {
        return;
        }

        //stop the robot
        var cmd_vel_web = new window.ROSLIB.Topic({
            ros : this.props.ros,
            name : Config.CMD_VEL_TOPIC,
            messageType : Config.CMD_VEL_MSG_TYPE,
        });

        var twist = new window.ROSLIB.Message({
            linear: {x: 0, y: 0, z: 0},
            angular: {x: 0, y: 0, z: 0}
        });

        // publish Twist message
        cmd_vel_web.publish(twist);
    }

    render(){
        return(
            <div>
                <Joystick
                    size={200} 
                    sticky={false} 
                    baseColor="#EEEEEE" 
                    stickColor="#BBBBBB" 
                    move={this.handleMove} 
                    stop={this.handleStop}>
                </Joystick>
            </div>
        );
    }



}



export default Teleoperation;
